import initialState from './initialState';
import { MODAL_OPEN, MODAL_CLOSE, UPDATE_FIELDS, SUBMIT } from '../actions/formActions';

const appointmentsReducer = (state = initialState, action) => {
  switch(action.type) {
    case MODAL_OPEN: {
      console.log('Inside Modal Toggle', action.payload.target.id);
      let tempId = action.payload.target.id;
      let tempArr = state.appointments;
      for(let i = 0; i < tempArr.length; i++) {
        if(tempArr[i].id === tempId){
          console.log("inside modal open ", tempArr[i]);
          return {
            ...state,
            modal_visible: !state.modal_visible,
            id: action.payload.target.id,
            fname: temp[i].fname,
            lname: temp[i].lname,
            phone: temp[i].phone
          }
        }
      }

    }
    case MODAL_CLOSE: {
      //console.log('Inside Modal Toggle');
      return {
        ...state,
        modal_visible: !state.modal_visible,
        id: '',
        fname: '',
        lname: '',
        phone: '',
      }
    }
    case UPDATE_FIELDS: {
      //action.payload.event.preventDefault();
      console.log("inside modal Reducer update fields ", action.payload.target.value);
      return {
        ...state,
        [action.payload.target.name]: action.payload.target.value

      }
    } 
    case SUBMIT: {
      //console.log("submitted ", action.payload);
      let temp = state.appointments;
      let block = document.getElementById(state.id);
      console.log(" in submit ", temp);
      for(let i = 0; i < temp.length; i++){
        let tempIndex = temp[i];
        console.log('in the for loop');
        if(tempIndex.id === state.id){
          console.log("inside if statement");

          tempIndex.fname = state.fname;
          tempIndex.lname = state.lname;
          tempIndex.phone = state.phone;
          console.log('temp ', temp);
          block.parentElement.classList.add('block-taken');
          block.parentElement.classList.remove('block');
          return {
            ...state,
            appointments: temp,
            modal_visible: false,
            id: '',
            fname: '',
            lname: '',
            phone: ''
          };
        };
      };
    };
  default:
    return state;
  }
};

export default appointmentsReducer;
