import initialState from './initialState';
import { MODAL_TOGGLE, UPDATE_FIELDS } from '../actions/formActions';

const modalReducer = (state = initialState, action) => {
  switch(action.type) {
    case MODAL_TOGGLE: {
      console.log('Inside Modal Toggle', action.payload.target.id.toString());
      return {
        ...state,
        modal_visible: !state.modal_visible,
        id: action.payload.target.id.toString()
      }
    }
  case UPDATE_FIELDS: {
    return {
      ...state,
      fname: action.payload.fname,
      lname: action.payload.lname,
      phone: action.payload.phone
    }
  }
  default:
    return state;
  }
};

export default modalReducer;
