import initialState from './initialState';
import { MODAL_OPEN, MODAL_CLOSE, UPDATE_FIELDS, SUBMIT } from '../actions/formActions';

const appointmentsReducer = (state = initialState, action) => {
  switch(action.type) {
    case MODAL_OPEN: {
      console.log('Inside Modal Toggle', action.payload.target.id.toString());
      return {
        ...state,
        modal_visible: !state.modal_visible,
        id: action.payload.target.id.toString()
      }
    }
    case MODAL_CLOSE: {
      //console.log('Inside Modal Toggle');
      return {
        ...state,
        modal_visible: !state.modal_visible,
        id: null,
        fname: null,
        lname: null,
        phone: null,
      }
    }
    case UPDATE_FIELDS: {
      //action.payload.event.preventDefault();
      console.log("inside modal Reducer update fields ", action.payload.target.value);
      return {
        ...state,
        [action.payload.target.name]: action.payload.target.value

      }
    }
    case SUBMIT: {
      console.log("inside submit ", this.props.id);
      let index = this.props.appointments.findIndex((obj) => {
        return obj[0] === this.props.id;
      });

      return{
        ...state,
        temp: this.props.id,
        [this.props.appointments[index].fname]: this.props.fname,
        [this.props.appointments[index].lname]: this.props.lname,
        [this.props.appointments[index].phone]: this.props.phone,
      }
    }




  default:
    return state;
  }
};

export default appointmentsReducer;
