import React, { Component } from 'react';

export default class Modal extends Component {
  state = {
    fname: null,
    lname: null,
    phone: null
  }

    handleChange = (e) => {
      this.setState({
        [e.target.id]: e.target.value
      });
    }

    handleSubmit = (e) => {
      e.preventDefault();
      console.log(this.state);

    }

  render() {
    const {handleSubmit} = this.props;
    return (
      <div className='modal-bg'>

        <div className='modal-body'>
        <span id='close'><button>X</button></span>
          <form id='modal-form' onSubmit={this.handleSubmit}>
            <label htmlFor='fname'>First Name:</label>
            <br></br>

            <input name="fname"  type='text' id='fname' onChange={this.handleChange}></input>
            <br></br>
            <label htmlFor='lname'>Last Name:</label>
            <br></br>
            <input type='text' name='lname' id='lname' onChange={this.handleChange}></input>
            <br></br>
            <label htmlFor='phone'>Phone:</label>
            <br></br>
            <input type='tel' name='phone' id='phone' pattern='[0-9]{3}-[0-9]{3}-[0-9]{4}' onChange={this.handleChange}></input>
            <br></br>
            <button className='submit-button'>Submit</button>
          </form>
        </div>

      </div>
    )
  }
}
