import initialState from './initialState';
import { MODAL_TOGGLE } from '../actions/formActions';
import { UPDATE_CURRENT } from '../actions/formActions';


const appointmentReducer = (state = initialState, action) => {
  switch(action.type) {
    case MODAL_TOGGLE: {
      //console.log('Inside Modal Toggle');
      return {
        ...state,
        modal_visible: !state.modal_visible
      }
    };
  case UPDATE_CURRENT: {
      console.log("appointment reducer", current);
      return {
        ...state,
        current: action.payload
      }
    }

  default:
    return state;
  }
};

export default appointmentReducer;
