import React, { Component } from 'react';
import './App.css';
import AppointmentForm from './Modal';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal_visible: false,
      current: null,
      fname: null,
      lname: null,
      phone: null,
      appointments: [
        {id: 'nine'},
        {id: 'ten'},
        {id: 'eleven'},
        {id: 'twelve'},
        {id: 'one'},
        {id: 'two'},
        {id: 'three'},
        {id: 'four'}
      ]
    }

  }
//Pass the id of the block to the modal window


 onPress = (e) => {
  e.preventDefault();
  console.log(e.target.id);
  block = e.target.id;
  this.setState({
    modal_visible: true,
    current: e.target.id
  });

 }
  render() {

    return (
      <div className="App">
        <header className="header">
          <h1>Today's Appointments</h1>
        </header>
        <div className='calendar'>
        <div>
          <ul>
            <li className='block' id='nine'  onClick={(e) => this.onPress(e)}><p>9 a.m.</p></li>
            <li className='block' id='ten' idnum={'ten'} onClick={this.onPress}><p idnum={'ten'} id='ten'>10 a.m.</p></li>
            <li className='block' id='eleven' idnum={'eleven'} onClick={this.onPress}><p>11 a.m.</p></li>
            <li className='block' id='twelve' idnum={'twelve'} onClick={this.onPress}><p>12 p.m.</p></li>
            <li className='block' id='one' idnum={'one'} onClick={this.onPress}><p>1 p.m.</p></li>
            <li className='block' id='two' idnum={'two'} onClick={this.onPress}><p>2 p.m.</p></li>
            <li className='block' id='three' idnum={'three'} onClick={this.onPress}><p>3 p.m.</p></li>
            <li className='block' id='four' idnum={'four'} onClick={this.onPress}><p>4 p.m.</p></li>
          </ul>
        </div>
      </div>
      { this.state.modal_visible ? <AppointmentForm idnum={this.state.current}/> : null}

      </div>
    );
  }
}

export default App;
