import initialState from './initialState';
import MODAL_TOGGLE from '../actions/formActions';

const modalReducer = (state = initialState, action) => {
  switch(action.type) {
    case MODAL_TOGGLE: {
      return Object.assign({}, state, {modal_visibile: true /*!state.modal_visibile*/});
    }
  default:
    return state;
  }
};

export default modalReducer;
