  switch(action.type) {
    case MODAL_OPEN: {
      console.log('Inside Modal Toggle', action.payload.target.id.toString());
      return {
        ...state,
        modal_visible: !state.modal_visible,
        id: action.payload.target.data-id
      }
    }
    case MODAL_CLOSE: {
      //console.log('Inside Modal Toggle');
