import './App.css';
//import AppointmentForm from './Modal';
import { connect } from 'react-redux';
import { MODAL_OPEN, MODAL_CLOSE, UPDATE_FIELDS, SUBMIT } from '../actions/formActions';


class App extends Component {
  /*constructor(props) {
    super(props);
    this.state = {
      modal_visible: false,
      current: null,
      current_data: null,
      appointments: [
        {id: 'nine'},
