  let tempNum = e.target.id;
  //console.log("tempNum : ", tempNum);
  let arr = this.state.appointments;
  for(let i = 0; i < arr.length; i++) {
    //console.log("inside for: ", arr[i].id);
    if(tempNum === arr[i].id) {
      //console.log("Ha");
      this.setState({
        current_data: arr[i],
        modal_visible: true,
        current: e.target.id
      });
    }
  }
 }*/


  /*setNewEntry = (data) => {

    //console.log("data ", data);
    let index = data.id;
    //console.log("index : ", index);
    let appArr = this.state.appointments;
    for(var i = 0; i < appArr.length; i++) {
      let temp = appArr[i].id;
      //console.log("appArr ", temp, index);
      if(temp === index){
        appArr[i] = data;
        document.getElementById(index).parentNode.className = "block-taken";
        this.props.openModal();
        this.setState({
          appointments: appArr,
          current: null,
          //modal_visible: false
        });
        } 
        //console.log("try : ", this .state);
      }
    }*/


  render() {
    const submitWait = (e) =>{
      e.preventDefault();
      console.log("This has been paused");
      this.props.submit();
    }
    //let blocks = document.getElementsByClassName('block');
    //console.log('blocks ', blocks);

   console.log("props in app" , this.props);
    return (
      <div className="App">
        <header className="header">
          <h1>Today's Appointments</h1>
        </header>
        <div className='calendar'>
        <div>
          <ul>
            <li className="block" onClick={(e) => this.props.onPress(e)}><p id='nine' dataid={0}>9 a.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='ten' dataid={1}>10 a.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='eleven' dataid={2}>11 a.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='twelve' dataid={3}>12 p.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='one' dataid={4}>1 p.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='two' dataid={5}>2 p.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='three' data-d={6}>3 p.m.</p></li>
            <li className='block' onClick={(e) => this.props.onPress(e)}><p id='four' dataid={7}>4 p.m.</p></li>
          </ul>
        </div>
      </div>
      { this.props.modal_visible ? <div className='modal-bg'>

      <div className='modal-body'>
      <span id='close' onClick={this.props.onClose}><button>X</button></span>
        <form onSubmit={e => {e.preventDefault(); this.props.submit()}}>
          <label htmlFor='fname'>First Name:</label>
          <br></br>

          <input name="fname"  type='text' id='fname' value={this.props.fname} onChange={e => this.props.onKeyup(e)} required></input>
          <br></br>
          <label htmlFor='lname'>Last Name:</label>
          <br></br>
          <input type='text' name='lname' id='lname' value={this.props.lname} onChange={e => this.props.onKeyup(e)} required></input>
          <br></br>
          <label htmlFor='phone'>Phone:</label>
          <br></br>
          <input type='tel' name='phone' id='phone' value={this.props.phone} pattern='[0-9]{3}-[0-9]{3}-[0-9]{4}' onChange={e => this.props.onKeyup(e)} required></input>
          <br></br>
          <button className='submit-button' /*onClick={e => {e.preventDefault(); this.props.submit()}}*/ type='submit'>Submit</button>
        </form>
      </div>

    </div> : null}

      </div>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    temp: store.appointmentsReducer.temp,
    modal_visible: store.appointmentsReducer.modal_visible,
    id: store.appointmentsReducer.id,
    fname: store.appointmentsReducer.fname,
    lname: store.appointmentsReducer.lname,
    phone: store.appointmentsReducer.phone,
    appointments: store.appointmentsReducer.appointments
  }
}

const mapDispatchToProps = (dispatch) => ({
  onPress: (id) => {
    dispatch({type: MODAL_OPEN, payload: id})
  },
  onClose: () => {
    dispatch({type: MODAL_CLOSE})
  },
  onKeyup: (data) => {
    dispatch({type: UPDATE_FIELDS, payload: data})
  },
  submit: (obj) => {
    dispatch({type: SUBMIT, payload: obj/*{id: this.props.id, fname: this.props.fname, lname: this.props.lname, phone: this.props.phone}*/  });
  }
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
