import React, { Component } from 'react';
import { connect } from 'react-redux';
import { MODAL_TOGGLE, UPDATE_FIELDS  } from '../actions/formActions';

class Modal extends Component {
  /*constructor(props){
    super(props);
    this.state = {
      id: null,
      fname: null,
      lname: null,
      phone: null
    }
  }*/

  /*componentDidMount() {
